import React, { Component } from "react";
import image from "../pixel2.jpg";
import image2 from "../pixel3.jpg";

class Home extends Component {

    constructor(props) {
        super(props)
      this.state = {images: ["https://i.imgur.com/Y0iI6Mxb.jpg", "https://i.imgur.com/qWP4aRSb.jpg","https://i.imgur.com/flTzFAib.jpg"
    ],
    selectedImage: "https://i.imgur.com/flTzFAib.jpg"};
    }

    componentDidMount() {
        let intervalId = setInterval(() => {
            this.setState(prevState => {
              if (prevState.selectedImage === this.state.images[0]) {
                return {
                  selectedImage: this.state.images[1]
                };
              } else {
                return {
                  selectedImage: this.state.images[0]
                };
              }
            });
          }, 1000);

          this.setState({
            intervalId
          });
        }


    componentWillMount() {
        clearInterval(this.interval);
    }

    render() {
        return (
            <div className ="App">
                <img src={this.state.selectedImage} alt={"images"} />
            </div>
            );
        } 
    }



export default Home;
